import React from 'react';

const HeroCarouselCard = () => {
    const [heroImages, setHeroImages] = React.useState([]);

    return (
        <>
            <div className={"flex flex-col gap-3"}>
                <div className={"hidden md:flex"}>
                    <img
                        className={"w-full h-full"}
                        src={"https://in.bmscdn.com/discovery-catalog/collections/tr:w-1440,h-120/et00308393-ewxqjzqjxk-portrait.jpg"}
                        alt={"Hero Carousel"}
                    />
                </div>
                <div className={"flex flex-col gap-3 md:hidden"}>
                    <img
                        className={"w-full h-full"}
                        src={"https://in.bmscdn.com/discovery-catalog/collections/tr:w-300,h-300/et00308393-ewxqjzqjxk-portrait.jpg"}
                        alt={"Hero Carousel"}
                    />
                    <img
                        className={"w-full h-full"}
                        src={"https://in.bmscdn.com/discovery-catalog/collections/tr:w-300,h-300/et00308393-ewxqjzqjxk-portrait.jpg"}
                        alt={"Hero Carousel"}
                    />
                    <img
                        className={"w-full h-full"}
                        src={"https://in.bmscdn.com/discovery-catalog/collections/tr:w-300,h-300/et00308393-ewxqjzqjxk-portrait.jpg"}
                        alt={"Hero Carousel"}
                    />
                </div>
            </div>
            
        </>
    );
};

export default HeroCarouselCard;
