import React from 'react';
import {Route, Switch} from 'react-router-dom';
import DefaultLayout from "../layout/DefaultLayout";

import EntertainmentCard from "../components/entertainment/Entertainment";
import PosterSlider from "../components/posterSlider/posterSlider";
import HeroCarousel from "../components/heroCarousel/HeroCarousel";

const HomePage = () => {
    const [recommendedMovies, setRecommendedMovies] = React.useState([]);
    const [premiumMovies, setPremiumMovies] = React.useState([]);
    const [onlineStreamEvents, setOnlineStreamEvents] = React.useState([]);
    return (
        <>
            <HeroCarousel/>
            <div className={"container mx-auto px-4 md:px-12 my-8"}>
                <h1 className={"text-2xl font-bold text-gray-800 sm:ml-3 ml-0 my-3"}>The best of Entertainment</h1>
                <EntertainmentCard/>
            </div>
            <div className={"container mx-auto px-4 md:px-12 my-8"}>
                <PosterSlider
                    title={"Recommended Movies"}
                    subject={"List of recommended Movies"}
                    posters={recommendedMovies}
                    isDark={false}
                />
            </div>
            <div className={"bg-premier-800 py-12"}>
                <div className={"container mx-auto px-4 md:px-12 my-8 flex-col gap-3"}>
                    <div className={"hidden md:flex"}>
                        <img
                            src={"https://in.bmscdn.com/discovery-catalog/collections/tr:w-1440,h-120/premiere-rupay-banner-web-collection-202104230555.png"}
                            alt={"Rupay"}
                            className={"w-full h-full"}
                        />
                    </div>
                    <div className={"container mx-auto px-4"}>

                    </div>
                    <PosterSlider
                        title={"Premiers"}
                        subject={"Brand new releases every Friday"}
                        posters={premiumMovies}
                        isDark={true}
                    />

                </div>
            </div>
        </>
    );
};

export default DefaultLayout(HomePage);
